function run(reposContent) {

    const reposContentObj = JSON.parse(reposContent);

    const newReposContent = reposContentObj.map(mapObj);

    return {
        "itemType": "application/vnd.lime.document-select+json",
        "items": newReposContent
    } 
}

function mapObj(reposContent){
    return{
        "header": {
            "type": "application/vnd.lime.media-link+json",
            "value": {
                "title": reposContent.name,
                "text": reposContent.description,
                "type": "image/jpeg",
                "uri": reposContent.avatar
            }
        }
    }
}
